def tokenString = context.expand('${SessionCreate#Response#//wsse:Security[1]/wsse:BinarySecurityToken[1]/text()}')

def tokenBase64 = tokenString.bytes.encodeBase64().toString()

testRunner.testCase.testSuite.project.setPropertyValue( "tokenBase64", tokenBase64 )
testRunner.testCase.testSuite.project.setPropertyValue( "tokenString", tokenString )
log.info("---> " + tokenString)
log.info("---> " + tokenBase64)

	 import java.util.Date;
	 import java.text.SimpleDateFormat;
	 import groovy.util.XmlParser;
	 import groovy.util.XmlSlurper;
	 import groovy.xml.*;



	def projectDir = context.expand('${projectDir}')
	def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ) ;



//Hitting the Hotel Avail to generate a RateKey
	testRunner.runTestStepByName("GetHotelDetailsRQ")	
	def resultAsXml = context.expand('${GetHotelDetailsRQ#Response}')		

//Remove name spaces from response					
	resultAsXml = resultAsXml.replaceAll("(ns\\d*:|ns\\d*:)","")
	resultAsXml = resultAsXml.replaceAll("soap-env:","")
	resultAsXml = resultAsXml.replaceAll("stlh2:","")	
// Fetch RateKey from Avail response
	def finalResult = resultAsXml.substring(resultAsXml.indexOf("<GetHotelDetailsRS"),resultAsXml.lastIndexOf("</Body>"))	
	def leadRateXmlResp = new XmlParser().parseText(finalResult)
	//log.info "leadRateXmlResp --> " + leadRateXmlResp.name()
	//def rateKey = leadRateXmlResp.GetHotelDetailsRS.HotelDetailsInfo.HotelRateInfo[1]
	def rateKey = leadRateXmlResp.HotelDetailsInfo.HotelRateInfo.RateInfos.RateInfo.@'RateKey'[0]	

	testRunner.testCase.testSuite.setPropertyValue("RateKey",rateKey)
//log.info "Rate key --> " + rateKey

	 import java.util.Date;
	 import java.text.SimpleDateFormat;
	 import groovy.util.XmlParser;
	 import groovy.util.XmlSlurper;
	 import groovy.xml.*;



	def projectDir = context.expand('${projectDir}')
	def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ) ;



//Hitting the Hotel Avail to generate a RateKey
	testRunner.runTestStepByName("HotelPriceCheckRQ")	
	def resultAsXml = context.expand('${HotelPriceCheckRQ#Response}')		

//Remove name spaces from response					
	resultAsXml = resultAsXml.replaceAll("(ns\\d*:|ns\\d*:)","")
	resultAsXml = resultAsXml.replaceAll("soap-env:","")
	resultAsXml = resultAsXml.replaceAll("stlh2:","")	
// Fetch RateKey from Avail response
	def finalResult = resultAsXml.substring(resultAsXml.indexOf("<HotelPriceCheckRS"),resultAsXml.lastIndexOf("</Body>"))	
	def priceCheckXmlResp = new XmlParser().parseText(finalResult)
	//log.info "leadRateXmlResp --> " + leadRateXmlResp.name()
	//def rateKey = leadRateXmlResp.GetHotelDetailsRS.HotelDetailsInfo.HotelRateInfo[1]
	def bookingKey = priceCheckXmlResp.PriceCheckInfo.@'BookingKey'[0]
//	log.info "booking Key --> " + bookingKey
	testRunner.testCase.testSuite.project.setPropertyValue("BookingKey",bookingKey)
	//log.info "Booking Key --> " + BookingKey
